import logo from "./logo.svg";
import * as S from "./style";
import InputIcon from "./img/Vector.svg";
import "./App.css";
import { useState } from "react";

function App() {
  const [imgFile, setImgFile] = useState("");
  const [previewUrl, setPreviewUrl] = useState("");

  const showImg = (e) => {
    e.preventDefault();
    let reader = new FileReader();
    let file = e.target.files[0];
    console.log(e.target.files[0]);
    console.log(reader);

    reader.onloadend = () => {
      setImgFile(file);
      setPreviewUrl(reader.result);
    };

    reader.readAsDataURL(file);
  };

  return (
    <S.Wrapper>
      {previewUrl === "" ? (
        <S.PhotoInputLabel htmlFor="uploadButton">
          <S.InputIcon src={InputIcon} />
          <S.InputText>이미지를 선택해 주세요</S.InputText>
        </S.PhotoInputLabel>
      ) : (
        <S.PreviewImg style={{
          backgroundImage: `url(${previewUrl})`,}}/>
      )}

      <S.PhotoInput
        id="uploadButton"
        type="file"
        accept="image/png, image/jpg, image/svg, image/jpeg"
        onChange={showImg}
      />
    </S.Wrapper>
  );
}

export default App;
